```tsx baseui-test/src/app/components/Button/Button.tsx
"use client";

import React from "react";
import styles from "./Button.module.css";
import "@/app/components/theme.css";

export type ButtonProps = {
    children: string;
    variant?: "primary" | "secondary" | "tertiary";
    color?: "error" | "success" | "info" | "warning";
    disabled?: boolean;
    loading?: boolean;
    dataSize?: "small" | "medium" | "large";
    onClick?: () => void;
    className?: string;
    type?: "button" | "submit" | "reset";
    icon?: React.ReactNode;
    iconPosition?: "start" | "end";
    theme?: "light" | "dark";
};

export const Button = ({
    children,
    variant = "primary",
    color,
    disabled = false,
    loading = false,
    dataSize = "medium",
    onClick,
    className = "",
    type = "button",
    icon,
    iconPosition = "end",
    theme = "light",
    ...props
}: ButtonProps &
    Omit<React.ButtonHTMLAttributes<HTMLButtonElement>, keyof ButtonProps>) => {
    // Creating a clean props object with only the necessary properties
    const buttonProps: Record<string, any> = {
        className: [
            styles.button,
            styles[variant],
            color && styles[color],
            styles[dataSize],
            icon &&
                (iconPosition === "end" ? styles.iconEnd : styles.iconStart),
            loading && styles.loading,
            className,
        ]
            .filter(Boolean)
            .join(" "),
        disabled: disabled || loading,
        type,
        "aria-busy": loading,
        ...props,
    };

    // Only add event handler if not disabled or loading
    if (!disabled && !loading && onClick) {
        buttonProps.onClick = onClick;
    }

    // Only add data attributes with non-default values
    if (dataSize !== "medium") {
        buttonProps["data-size"] = dataSize;
    }

    if (theme !== "light") {
        buttonProps["data-theme"] = theme;
    }

    return (
        <button {...buttonProps}>
            {loading ? (
                <>
                    <span className={styles.loadingText}>{children}</span>
                    <Spinner className={styles.spinner} />
                </>
            ) : (
                <>
                    {children}
                    {icon && <span className={styles.icon}>{icon}</span>}
                </>
            )}
        </button>
    );
};

function Spinner({ className = "" }: { className?: string }) {
    return (
        <svg
            className={className}
            width="16"
            height="16"
            viewBox="0 0 16 16"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
        >
            <circle
                className={styles.spinnerTrack}
                cx="8"
                cy="8"
                r="7"
                stroke="currentColor"
                strokeOpacity="0.25"
                strokeWidth="2"
            />
            <path
                className={styles.spinnerIndicator}
                d="M15 8C15 4.13401 11.866 1 8 1"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
            />
        </svg>
    );
}

export default Button;
```


Here's your updated Button component that capitalizes only the first letter of the button text:

```tsx
"use client";

import React from "react";
import styles from "./Button.module.css";
import "@/app/components/theme.css";

export type ButtonProps = {
    children: string;
    variant?: "primary" | "secondary" | "tertiary";
    color?: "error" | "success" | "info" | "warning";
    disabled?: boolean;
    loading?: boolean;
    dataSize?: "small" | "medium" | "large";
    onClick?: () => void;
    className?: string;
    type?: "button" | "submit" | "reset";
    icon?: React.ReactNode;
    iconPosition?: "start" | "end";
    theme?: "light" | "dark";
};

export const Button = ({
    children,
    variant = "primary",
    color,
    disabled = false,
    loading = false,
    dataSize = "medium",
    onClick,
    className = "",
    type = "button",
    icon,
    iconPosition = "end",
    theme = "light",
    ...props
}: ButtonProps &
    Omit<React.ButtonHTMLAttributes<HTMLButtonElement>, keyof ButtonProps>) => {
    // Creating a clean props object with only the necessary properties
    const buttonProps: Record<string, any> = {
        className: [
            styles.button,
            styles[variant],
            color && styles[color],
            styles[dataSize],
            icon &&
                (iconPosition === "end" ? styles.iconEnd : styles.iconStart),
            loading && styles.loading,
            className,
        ]
            .filter(Boolean)
            .join(" "),
        disabled: disabled || loading,
        type,
        "aria-busy": loading,
        ...props,
    };

    // Only add event handler if not disabled or loading
    if (!disabled && !loading && onClick) {
        buttonProps.onClick = onClick;
    }

    // Only add data attributes with non-default values
    if (dataSize !== "medium") {
        buttonProps["data-size"] = dataSize;
    }

    if (theme !== "light") {
        buttonProps["data-theme"] = theme;
    }

    // Function to capitalize only the first letter of the first word
    const capitalizeFirstLetter = (text: string) => {
        if (!text) return text;
        const words = text.split(' ');
        if (words.length === 0) return text;

        words[0] = words[0].charAt(0).toUpperCase() + words[0].slice(1);
        return words.join(' ');
    };

    return (
        <button {...buttonProps}>
            {loading ? (
                <>
                    <span className={styles.loadingText}>
                        {capitalizeFirstLetter(children)}
                    </span>
                    <Spinner className={styles.spinner} />
                </>
            ) : (
                <>
                    <span>{capitalizeFirstLetter(children)}</span>
                    {icon && <span className={styles.icon}>{icon}</span>}
                </>
            )}
        </button>
    );
};

function Spinner({ className = "" }: { className?: string }) {
    return (
        <svg
