    position: relative;
    display: inline-block;
    transition: background 0.2s;
}

.toggle-container.default .toggle {
    width: 40px;
    height: 26px;
}

.toggle-container.default .toggle-switch {
    height: 20px;
    width: 20px;
    left: 2px;
    bottom: 2px;
