}

.toggle-container.default
    .toggle-checkbox:checked
    + .toggle-label
    .toggle-switch {
    transform: translateX(18px);
    background-color: #287891;
}

.toggle-container.compact .toggle {
    width: 40px;
    height: 24px;
}

.toggle-container.compact .toggle-switch {
    height: 18px;
    width: 18px;
    left: 3px;
    bottom: 3px;
}

.toggle-container.compact
    .toggle-checkbox:checked
    + .toggle-label
    .toggle-switch {
    transform: translateX(16px);
}

.toggle-checkbox {
    opacity: 0;
    width: 0;
    height: 0;
}

.toggle-label {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: var(--secondary-color);
    transition: 0.4s;
    border-radius: var(--border-radius);
}

