```tsx fundament/src/app/components/Button/Button.stories.tsx
import type { Meta, StoryObj } from "@storybook/react";
import {
    Title,
    Primary as PrimaryBlock,
    Stories,
    Subtitle,
    Controls,
} from "@storybook/blocks";

import { Button } from "./Button";

const meta = {
    title: "Components/Button",
    component: Button,
    tags: ["autodocs"],
    args: {
        children: "Button",
    },
    parameters: {
        docs: {
            page: () => (
                <>
                    <Title />
                    <Subtitle>
                        Knapper brukes for å utføre handlinger i
                        brukergrensesnittet.
                    </Subtitle>
                    <p>
                        Vi tilbyr tre varianter <code>primary</code>,
                        <code>secondary</code> og <code>tertiary</code> som hver
                        har ulik visuell vekt avhengig av konteksten.
                    </p>
                    <p>
                        Alle variantene er tilgjengelige i flere størrelser,
                        fargevarianter og støtter både lys og mørk modus.
                    </p>
                    <PrimaryBlock />
                    <Controls of={Primary} />
                    <Stories />
                </>
            ),
            source: {
                excludeDecorators: true,
                transform: (code, story) => {
                    // Remove props with default values
                    return code
                        .replace(/\s+variant="primary"/g, "")
                        .replace(/\s+color=""/g, "")
                        .replace(/\s+color={undefined}/g, "")
                        .replace(/\s+icon={null}/g, "")
                        .replace(/\s+iconPosition="end"/g, "")
                        .replace(/\s+dataSize="medium"/g, "")
                        .replace(/\s+disabled={false}/g, "")
                        .replace(/\s+loading={false}/g, "")
                        .replace(/\s+theme="light"/g, "");
                },
            },
        },
    },
    argTypes: {
        variant: {
            control: "radio",
            options: ["primary", "secondary", "tertiary"],
            description: "Button variant styling",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "primary" },
            },
        },
        color: {
            control: "radio",
            options: ["", "error", "success", "info", "warning"],
            description: "Legg til en status farge",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "" },
            },
        },
        dataSize: {
            control: "radio",
            options: ["small", "medium", "large"],
            description: "Knappens størrelse",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "medium" },
            },
        },
        disabled: {
            control: "boolean",
            description: "Deaktiverer knappen",
            table: {
                type: { summary: "boolean" },
                defaultValue: { summary: false },
            },
        },
        loading: {
            control: "boolean",
            description: "Viser en spinner og deaktiverer knappen",
            table: {
                type: { summary: "boolean" },
                defaultValue: { summary: false },
            },
        },
        className: {
            control: "text",
            description:
                "Legg til en CSS-klasse for å styre stilene på knappen",
            table: {
                type: { summary: "ReactNode" },
                defaultValue: { summary: "null" },
            },
        },
        onClick: {
            control: "text",
            description:
                "Legg til en funksjon som skal kjøres når knappen trykkes",
            table: {
                type: { summary: "ReactNode" },
                defaultValue: { summary: "null" },
            },
        },
        icon: {
            control: "text",
            description: "Legg til et ikon",
            table: {
                type: { summary: "ReactNode" },
                defaultValue: { summary: "null" },
            },
        },
        iconPosition: {
            control: "radio",
            options: ["start", "end"],
            description: "Velg plassering for ikonet",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "end" },
            },
            if: { arg: "icon", truthy: true },
        },
    },
} satisfies Meta<typeof Button>;

export default meta;
type Story = StoryObj<typeof meta>;

// Primary Button (Default)
export const Primary: Story = {
    args: {
        children: "Primary Button",
    },
};

// Secondary Button
export const Secondary: Story = {
    args: {
        children: "Secondary Button",
        variant: "secondary",
    },
};

// Tertiary Button
export const Tertiary: Story = {
    args: {
        children: "Tertiary Button",
        variant: "tertiary",
        icon: "＋",
    },
};

// Different Sizes
export const Størrelser: Story = {
    render: () => (
        <div style={{ display: "flex", gap: "12px", alignItems: "center" }}>
            <Button dataSize="small">Small</Button>
            <Button dataSize="medium">Medium</Button>
            <Button dataSize="large">Large</Button>
        </div>
    ),
};

// Theme modes
export const Darkmode: Story = {
    render: () => (
        <div style={{ display: "flex", flexDirection: "column", gap: "24px" }}>
            <div>
                <h3>Light Mode</h3>
                <div
                    style={{
                        display: "flex",
                        gap: "12px",
                        marginBottom: "16px",
                    }}
                >
                    <Button variant="primary">Primary</Button>
                    <Button variant="secondary">Secondary</Button>
                    <Button variant="tertiary">Tertiary</Button>
                </div>
            </div>

            <div>
                <h3>Dark Mode</h3>
                <div
                    style={{
                        display: "flex",
                        gap: "12px",
                        background: "#1f2937",
                        padding: "16px",
                        borderRadius: "8px",
                    }}
                    data-theme="dark"
                >
                    <Button variant="primary">Primary</Button>
                    <Button variant="secondary">Secondary</Button>
                    <Button variant="tertiary">Tertiary</Button>
                </div>
            </div>
        </div>
    ),
};

// Disabled State
export const Deaktivert: Story = {
    args: {
        children: "Disabled Button",
        disabled: true,
    },
};

// With Icon
export const Ikon: Story = {
    render: () => <Button icon="＋">Legg til</Button>,
};

// Loading State
export const Loading: Story = {
    render: () => <Button loading={true}>Laster...</Button>,
};

// Button Group
export const Gruppe: Story = {
    render: () => (
        <div style={{ display: "flex", gap: "8px" }}>
            <Button variant="secondary">Cancel</Button>
            <Button>Save</Button>
        </div>
    ),
};

// Status Buttons
export const StatusButtons: Story = {
    render: () => (
        <div style={{ display: "flex", flexDirection: "column", gap: "20px" }}>
            <div>
                <h3>Status Buttons</h3>
                <div style={{ display: "flex", gap: "12px" }}>
                    <Button variant="error">Error</Button>
                    <Button variant="success">Success</Button>
                    <Button variant="info">Info</Button>
                    <Button variant="warning">Warning</Button>
                </div>
            </div>

            <div>
                <h3>Dark Mode</h3>
                <div
                    style={{
                        display: "flex",
                        gap: "12px",
                        background: "#1f2937",
                        padding: "16px",
                        borderRadius: "8px",
                    }}
                    data-theme="dark"
                >
                    <Button variant="error">Error</Button>
                    <Button variant="success">Success</Button>
                    <Button variant="info">Info</Button>
                    <Button variant="warning">Warning</Button>
                </div>
            </div>

            <div>
                <h3>With Icons</h3>
                <div style={{ display: "flex", gap: "12px" }}>
                    <Button variant="error" icon="✖">
                        Delete
                    </Button>
                    <Button variant="success" icon="✓">
                        Approve
                    </Button>
                    <Button variant="info" icon="ℹ">
                        Details
                    </Button>
                    <Button variant="warning" icon="⚠">
                        Caution
                    </Button>
                </div>
            </div>
        </div>
    ),
};
```

How can I add the "Ikon" title to the story as "Knapp med ikon" ?
Looking at your `Button.stories.tsx` file, you want to change the title of the `Ikon` story from "Ikon" to "Knapp med ikon".

To achieve this, you need to add a `name` property to your story definition. This property is what determines how the story is displayed in the Storybook navigation panel and as a heading in the docs page.

Here's how you can modify the `Ikon` story:

```tsx
// With Icon
export const Ikon: Story = {
    name: "Knapp med ikon",  // Add this line
    render: () => <Button icon="＋">Legg til</Button>,
};
```

By adding the `name` property with the value "Knapp med ikon", this story will display with that title in the Storybook UI instead of using the variable name "Ikon".
