```tsx baseui-test/src/app/components/Button/Button.tsx
"use client";

import React from "react";
import styles from "./Button.module.css";
import "@/app/components/theme.css";

export type ButtonProps = {
    children: React.ReactNode;
    variant?: "primary" | "secondary" | "tertiary";
    disabled?: boolean;
    loading?: boolean;
    dataSize?: "small" | "medium" | "large";
    onClick?: () => void;
    className?: string;
    type?: "button" | "submit" | "reset";
    icon?: React.ReactNode;
    iconPosition?: "start" | "end";
    theme?: "light" | "dark";
};

export const Button = ({
    children,
    variant = "primary",
    disabled = false,
    loading = false,
    dataSize = "medium",
    onClick,
    className = "",
    type = "button",
    icon,
    iconPosition = "end",
    theme = "light",
    ...props
}: ButtonProps &
    Omit<React.ButtonHTMLAttributes<HTMLButtonElement>, keyof ButtonProps>) => {
    const buttonClasses = [
        styles.button,
        styles[variant],
        styles[dataSize],
        icon && (iconPosition === "end" ? styles.iconEnd : styles.iconStart),
        loading && styles.loading,
        className,
    ]
        .filter(Boolean)
        .join(" ");

    const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
        if (loading || disabled) return;
        if (onClick) onClick();
    };

    return (
        <button
            className={buttonClasses}
            disabled={disabled || loading}
            onClick={handleClick}
            type={type}
            aria-busy={loading}
            data-size={dataSize}
            data-theme={theme}
            {...props}
        >
            {loading ? (
                <>
                    <span className={styles.loadingText}>{children}</span>
                    <Spinner className={styles.spinner} />
                </>
            ) : (
                <>
                    {children}
                    {icon && <span className={styles.icon}>{icon}</span>}
                </>
            )}
        </button>
    );
};

function Spinner({ className = "" }: { className?: string }) {
    return (
        <svg
            className={className}
            width="16"
            height="16"
            viewBox="0 0 16 16"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
        >
            <circle
                className={styles.spinnerTrack}
                cx="8"
                cy="8"
                r="7"
                stroke="currentColor"
                strokeOpacity="0.25"
                strokeWidth="2"
            />
            <path
                className={styles.spinnerIndicator}
                d="M15 8C15 4.13401 11.866 1 8 1"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
            />
        </svg>
    );
}

export default Button;
```


