import type { Meta, StoryObj } from "@storybook/react";
import {
    Title,
    Primary as PrimaryBlock,
    Stories,
    Subtitle,
    Controls,
} from "@storybook/blocks";
import { Accordion } from "./Accordion";

const meta = {
    title: "Components/Accordion",
    component: Accordion,
    tags: ["autodocs"],
    args: {
        title: "Accordion Title",
        children: "Accordion Content",
    },
    parameters: {
        docs: {
            page: () => (
                <>
                    <Title />
                    <Subtitle>
                        Accordions brukes for å organisere innhold i
                        sammenleggbare seksjoner.
                    </Subtitle>
                    <p>
                        Accordions lar brukere vise og skjule innhold etter
                        behov, og er nyttige for å redusere visuell kompleksitet
                        og spare plass på skjermen.
                    </p>
                    <p>
                        Komponentet støtter både lys og mørk modus, og kan
                        konfigureres til å være åpen eller lukket som standard.
                    </p>
                    <PrimaryBlock />
                    <Controls of={Default} />
                    <Stories />
                </>
            ),
            source: {
                excludeDecorators: true,
                transform: (code, story) => {
                    // Remove props with default values
                    return code
                        .replace(/\s+defaultOpen={false}/g, "")
                        .replace(/\s+theme="light"/g, "");
                },
            },
        },
    },
    argTypes: {
        title: {
            control: "text",
            description: "Tittel som vises i accordion header",
            table: {
                type: { summary: "ReactNode" },
                defaultValue: { summary: "undefined" },
            },
        },
        children: {
            control: "text",
            description: "Innhold som vises når accordionen er åpen",
            table: {
                type: { summary: "ReactNode" },
                defaultValue: { summary: "undefined" },
            },
        },
        defaultOpen: {
            control: "boolean",
            description: "Styrer om accordionen er åpen som standard",
            table: {
                type: { summary: "boolean" },
                defaultValue: { summary: false },
            },
        },
        theme: {
            control: "radio",
            options: ["light", "dark"],
            description: "Definerer fargetema",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "light" },
            },
        },
        onOpenChange: {
            control: null,
            description:
                "Callback som kalles når accordionen åpnes eller lukkes",
            table: {
                type: { summary: "function" },
                defaultValue: { summary: "undefined" },
            },
        },
        className: {
            control: "text",
            description: "Egendefinert CSS-klasse",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "undefined" },
            },
        },
    },
} satisfies Meta<typeof Accordion>;

export default meta;
type Story = StoryObj<typeof meta>;

// Default Accordion
export const Default: Story = {
    args: {
        title: "Accordion Title",
        children: (
            <div>
                <p>
                    {" "}
                    I en dal som alltid ligger dekket av skygger, vokser det
                    blomster som gløder i mørket. De gamle forteller at
                    blomstene er skapt av tårer fra en prinsesse som en gang ble
                    fanget der av troll. De som plukker en slik blomst, sies å
                    få evnen til å se det usynlige, men bare hvis de klarer å
                    komme seg ut av dalen før natten faller på.
                </p>
            </div>
        ),
    },
};

// Open by Default
export const OpenByDefault: Story = {
    args: {
        title: "Open Accordion",
        children: <p>This accordion is open by default.</p>,
        defaultOpen: true,
    },
};

// Dark Theme
export const DarkTheme: Story = {
    render: () => (
        <div
            style={{
                background: "#1f2937",
                padding: "16px",
                borderRadius: "8px",
            }}
            data-theme="dark"
        >
            <Accordion title="Dark Theme Accordion" theme="dark">
                <p>This accordion uses the dark theme.</p>
            </Accordion>
        </div>
    ),
};

// Multiple Accordions
export const AccordionGroup: Story = {
    render: () => (
        <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
            <Accordion title="Section 1">
                <p>Content for section 1</p>
            </Accordion>
            <Accordion title="Section 2">
                <p>Content for section 2</p>
            </Accordion>
            <Accordion title="Section 3">
                <p>Content for section 3</p>
            </Accordion>
        </div>
    ),
};

// Rich Content
export const RichContent: Story = {
    args: {
        title: "Rich Content Example",
        children: (
            <div>
                <h3>Nested Headings</h3>
                <p>Paragraphs and other content can be included.</p>
                <ul>
                    <li>List item 1</li>
                    <li>List item 2</li>
                    <li>List item 3</li>
                </ul>
                <div
                    style={{
                        padding: "8px",
                        background: "#f3f4f6",
                        borderRadius: "4px",
                        marginTop: "8px",
                    }}
                >
                    <code>Code blocks can be included too</code>
                </div>
            </div>
        ),
    },
};

// Custom Styling
export const CustomStyling: Story = {
    render: () => (
        <Accordion
            title={
                <span style={{ color: "#4f46e5", fontWeight: "bold" }}>
                    Custom Title Styling
                </span>
            }
            className="custom-accordion"
        >
            <div style={{ color: "#1e3a8a" }}>
                <p>This accordion has custom styling applied.</p>
                <button
                    style={{
                        background: "#4f46e5",
                        color: "white",
                        border: "none",
                        padding: "8px 16px",
                        borderRadius: "4px",
                        marginTop: "8px",
                    }}
                >
                    A button inside the accordion
                </button>
            </div>
        </Accordion>
    ),
};

// Interactive Example
export const InteractiveExample: Story = {
    render: () => {
        const handleOpenChange = (open: boolean) => {
            console.log(`Accordion is now ${open ? "open" : "closed"}`);
        };

        return (
            <Accordion
                title="Interactive Example (check console)"
                onOpenChange={handleOpenChange}
            >
                <p>
                    When this accordion is opened or closed, a message is logged
                    to the console.
                </p>
            </Accordion>
        );
    },
};
