```tsx
import type { Meta, StoryObj } from "@storybook/react";
import {
    Title,
    Primary as PrimaryBlock,
    Stories,
    Subtitle,
    Controls,
} from "@storybook/blocks";
import { Checkbox } from "./Checkbox";

const meta = {
    title: "Components/Checkbox",
    component: Checkbox,
    tags: ["autodocs"],
    args: {
        label: "Checkbox Label",
    },
    parameters: {
        docs: {
            page: () => (
                <>
                    <Title />
                    <Subtitle>
                        Checkboxer brukes for å la brukere velge ett eller flere alternativer.
                    </Subtitle>
                    <p>
                        Checkboxer er interaktive kontroller som lar brukeren velge flere valg fra et sett. 
                        De brukes ofte i skjemaer og for filtrering.
                    </p>
                    <p>
                        Komponentet støtter både lys og mørk modus, og kan konfigureres til å være forhåndsvalgt, deaktivert eller obligatorisk.
                    </p>
                    <PrimaryBlock />
                    <Controls of={Default} />
                    <Stories />
                </>
            ),
            source: {
                excludeDecorators: true,
                transform: (code, story) => {
                    // Remove props with default values
                    return code
                        .replace(/\s+checked={false}/g, "")
                        .replace(/\s+disabled={false}/g, "")
                        .replace(/\s+required={false}/g, "")
                        .replace(/\s+theme="light"/g, "");
                },
            },
        },
    },
    argTypes: {
        label: {
            control: "text",
            description: "Tekst som vises ved siden av checkboxen",
            table: {
                type: { summary: "ReactNode" },
                defaultValue: { summary: "undefined" },
            },
        },
        checked: {
            control: "boolean",
            description: "Styrer om checkboxen er avkrysset",
            table: {
                type: { summary: "boolean" },
                defaultValue: { summary: false },
            },
        },
        disabled: {
            control: "boolean",
            description: "Deaktiverer checkbox når satt til true",
            table: {
                type: { summary: "boolean" },
                defaultValue: { summary: false },
            },
        },
        required: {
            control: "boolean",
            description: "Angir om checkboxen er obligatorisk",
            table: {
                type: { summary: "boolean" },
                defaultValue: { summary: false },
            },
        },
        theme: {
            control: "radio",
            options: ["light", "dark"],
            description: "Definerer fargetema",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "light" },
            },
        },
        onChange: {
            control: null,
            description: "Callback som kalles når checkbox-status endres",
            table: {
                type: { summary: "function" },
                defaultValue: { summary: "undefined" },
            },
        },
        className: {
            control: "text",
            description: "Egendefinert CSS-klasse",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "undefined" },
            },
        },
        id: {
            control: "text",
            description: "HTML ID-attributt for checkbox-elementet",
            table: {
                type: { summary: "string" },
                defaultValue: { summary: "undefined" },
            },
        },
    },
} satisfies Meta<typeof Checkbox>;

export default meta;
type Story = StoryObj<typeof meta>;

// Default Checkbox
export const Default: Story = {
    args: {
        label: "Standard checkbox",
    },
};

// Checked by Default
export const CheckedByDefault: Story = {
    args: {
        label: "Forhåndsvalgt checkbox",
        checked: true,
    },
};

// Disabled Checkbox
export const Disabled: Story = {
    args: {
        label: "Deaktivert checkbox",
        disabled: true,
    },
};

// Required Checkbox
export const Required: Story = {
    args: {
        label: "Obligatorisk checkbox",
        required: true,
    },
};

// Dark Theme
export const DarkTheme: Story = {
    render: () => (
        <div
            style={{
                background: "#1f2937",
                padding: "16px",
                borderRadius: "8px",
            }}
            data-theme="dark"
        >
            <Checkbox label="Mørkt tema checkbox" theme="dark" />
        </div>
    ),
};

// Multiple Checkboxes
export const CheckboxGroup: Story = {
    render: () => (
        <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
            <Checkbox label="Valg 1" />
            <Checkbox label="Valg 2" />
            <Checkbox label="Valg 3" />
        </div>
    ),
};

// Rich Label Content
export const RichLabelContent: Story = {
    args: {
        label: (
            <div>
                <span style={{ fontWeight: "bold" }}>Godta vilkår</span>
                <p style={{ margin: "4px 0 0", fontSize: "0.875rem", color: "#6b7280" }}>
                    Jeg godtar <a href="#" style={{ color: "#4f46e5" }}>bruksvilkårene</a> og <a href="#" style={{ color: "#4f46e5" }}>personvernerklæringen</a>
                </p>
            </div>
        ),
    },
};

// Custom Styling
export const CustomStyling: Story = {
    render: () => (
        <Checkbox
            label={
                <span style={{ color: "#4f46e5", fontWeight: "bold" }}>
                    Tilpasset styling
                </span>
            }
            className="custom-checkbox"
        />
    ),
};

// Interactive Example
export const InteractiveExample: Story = {
    render: () => {
        const handleChange = (checked: boolean) => {
            console.log(`Checkbox is now ${checked ? "checked" : "unchecked"}`);
        };

        return (
            <Checkbox
                label="Interaktivt eksempel (sjekk konsollen)"
                onChange={handleChange}
            />
        );
    },
};
```

This implementation follows the same structure as the Accordion stories file, including:
- Comprehensive documentation with Title, Subtitle, and description
- Detailed argTypes with control types, descriptions, and table information
- Multiple story examples showing different configurations
- Special stories like DarkTheme, RichLabelContent, and InteractiveExample
- Proper code transformation to exclude default props
- Norwegian text to match the style of the original
