"use client";

import React from "react";
import styles from "./Button.module.css";
import "@/app/components/theme.css";

export type ButtonProps = {
    children: React.ReactNode;
    variant?: "primary" | "secondary" | "tertiary";
    disabled?: boolean;
    onClick?: () => void;
    className?: string;
    type?: "button" | "submit" | "reset";
    icon?: React.ReactNode;
    iconPosition?: "start" | "end";
};

export const Button = ({
    children,
    variant = "primary",
    disabled = false,
    onClick,
    className = "",
    type = "button",
    icon,
    iconPosition = "end",
    ...props
}: ButtonProps &
    Omit<React.ButtonHTMLAttributes<HTMLButtonElement>, keyof ButtonProps>) => {
    const buttonClasses = [
        styles.button,
        styles[variant],
        icon && (iconPosition === "end" ? styles.iconEnd : styles.iconStart),
        className,
    ]
        .filter(Boolean)
        .join(" ");

    return (
        <button
            className={buttonClasses}
            disabled={disabled}
            onClick={onClick}
            type={type}
            {...props}
        >
            {children}
            {icon && <span className={styles.icon}>{icon}</span>}
        </button>
    );
};

export default Button;
